import React from 'react';

const dataReducer = (state = [], { type , data }) => { 
  switch(type){
      case 'SET':
          localStorage.setItem('myKey:546726736522537625467', JSON.stringify(data))
          state = data;
          break;
      default:
          return state;
  }
  return state;
}
export default dataReducer;
