import React, { useState } from 'react';
import axios from 'axios';
import Product from '../components/product';


const Home = () => {
  const [ pages, setPages ] = useState([]);
  const [ products, setProducts ] = useState([]);
  const [ page, setPage ] = useState(1);
  const getData = async (n: number) => {
    const {headers, data} = await axios.get(`http://0.0.0.0:8080/products/?_page=${n}&_limit=20`);
    setPages(parseInt(headers['x-total-count']) / 20);
    setProducts(data);
  }
  const getPage = (n: number) => {
    setPage(n);
    getData(n);
  }
  if (products.length === 0) getData(page);
  return (
    <div>
      Hi
      {
        products.map((product: any) => <Product product={product}/>)
      }
      {
        (new Array(pages)).map(el => <div>ji</div>)
        // allPages.map((_, index:number) => page === index ? "" : <a onClick={() => getPage(index + 1)}>{index + 1}</a>)
      }
    </div>
  );
};

export default Home;
