import React from 'react';
import { connect } from 'react-redux';
import axios from 'axios';

class Product extends React.Component {
  componentDidMount(){
    const self: any = this;
    const verif = JSON.parse(localStorage.getItem('myKey:546726736522537625467'));
    if (verif.length > 0) {
      console.log("storage Call");
      self.props.dispatch({
        type: 'SET',
        data: verif,
      });
    } else {
      console.log("api Call");
      axios.get(`http://0.0.0.0:8080/products`).then(({headers, data}) => {
        const pages_ = parseInt(headers['x-total-count']) / 20;
        self.setState({pages:pages_});
        localStorage.setItem('myKey:546726736522537625467', JSON.stringify(data))
        self.props.dispatch({
          type: 'SET',
          data,
        });
      })
    }

  }
  componentDidUpdate(prev){
    const { data }:any = this.props;
    if (prev.data != data){
      const urlParams = new URLSearchParams(window.location.search);
      const myParam = urlParams.get('id');
      console.log(myParam)
      this.filterData(myParam);
    }
  }
  filterData = (myParam) => {
    const {data:d}: any = this.props;
    const myData = d.filter(el => el.objectID == myParam);
    console.log(myData)
    this.setState({ product: myData })
  }
  render (){
    const { data }: any = this.props;

    // console.log(data.filter(el => ));
    return (
      <div style={{ padding: '30px' }}>
        hi
      </div>
    );
  };

}

export default connect(s => s)(Product);
