import React, { useState } from 'react';
import axios from 'axios';
import Product from '../components/product';


const Home = () => {
  const [ pages, setPages ] = useState(0);
  const [ products, setProducts ] = useState([]);
  const [ page, setPage ] = useState(1);
  const getData = async (n: number) => {
    const {headers, data} = await axios.get(`http://0.0.0.0:8080/products/?_page=${n}&_limit=20`);
    const pages_ = await parseInt(headers['x-total-count']) / 20;
    console.log(pages_)
    setPages(pages_);
    setProducts(data);
  }
  const getPage = (n: number) => {
    setPage(n);
    getData(n);
  }
  const Pagination = () => {
    let pages_ = [];
    for(var i = 1; i < pages + 1; i++){
      if (i !== page) pages_.push(
      <a style={{ marginRight: '15px' }} onClick={() => getPage(i)}>{i}</a>
      )
    }
    return <div>
      {pages_}
    </div>;
  }
  if (products.length === 0) getData(page);
  return (
    <div>
      Hi
      {
        products.map((product: any) => <Product product={product}/>)
      }
        <Pagination />
        {/* // allPages.map((_, index:number) => page === index ? "" : <a onClick={() => getPage(index + 1)}>{index + 1}</a>) */}
    </div>
  );
};

export default Home;
