import React, { useState } from 'react';
import axios from 'axios';
import Product from '../components/product';


const Home = () => {
  const [ pages, setPages ] = useState(0);
  const [ products, setProducts ] = useState([]);
  const [ page, setPage ] = useState(1);
  const getData = async (n: number) => {
    console.log(n)
    if (n == 1) {
      const {headers, data} = await axios.get(`http://0.0.0.0:8080/products/?_page=${n}&_limit=20`);
      const pages_ = await parseInt(headers['x-total-count']) / 20;
      setPages(pages_);
    }
    // console.log(data)
    // console.log(pages_)
    // setProducts(data);
  }
  const getPage = (event: number) => {
    setPage(n);
    getData(n);
  }
  const Pagination = ({ page_ }) => {
    let pages_ = [];
    for(var i = 1; i < pages + 1; i++){
      if (i != page_) pages_.push(
      <a key={i+Date.now()} href="#" style={{ marginRight: '15px' }} onClick={() => getPage}>{i}</a>
      )
    }
    return <div>
      {pages_}
    </div>;
  }
  if (products.length === 0) getData(page);
  return (
    <div>
      Hi
      {
        products.map((product: any) => <Product product={product}/>)
      }
      <Pagination page_={page} />
    </div>
  );
};

export default Home;
